import './Upload.scss';
import { Link } from 'react-router-dom';
import axios from 'axios'

export default function Upload(){

  const handleSubmit = (event) => {
    event.preventDefault()
    console.log('submit')

    if(!event.target.title.value) {
        const field = document.getElementById("title")
        field.style.border = "1px solid red"
        return
    }

    if(!event.target.title.value) {
      const field = document.getElementById("description")
      field.style.border = "1px solid red"
      return
    }

    const newVideo = {
        title: event.target.title.value,
        description: event.target.description.value
    }


    event.target.reset()

  }

  return (
    <form onSubmit={handleSubmit} className="upload">
        <h1 className="upload__title">Upload Video</h1>
        <div className="upload__fields">
          <div className="upload__image-container">
              <p className="upload__heading">VIDEO THUMBNAIL</p>
              <div className="upload__image"></div>
          </div>
          <div className="upload__inputs">
              <p className="upload__heading">TITLE YOUR VIDEO</p>
              <input id='title' name='title' className="upload__input" placeholder="Add a title to your video"/>
              <p className="upload__heading">ADD A VIDEO DESCRIPTION</p>
              <textarea id='description' name='description' className="upload__input upload__input-description" placeholder="Add a description to your video"/>
          </div>
        </div>
        <div className="upload__options">
          <button type='submit' className="upload__button">
              <span className="upload__button-icon"></span>PUBLISH</button>
          <Link to="/" className="upload__cancel">CANCEL</Link>
        </div>
    </form>
  );
}
