import Description from '../Description/Description';
import CommentSection from '../CommentSection/CommentSection';

export default function VideoDetails(props) {

    const addComment = (comment) => {
        axios.post(`${apiURL}/videos/${this.props.currentVideo.id}/comments/${apiKEY}`, comment)
        .catch((error) => {
            console.log(error)
        })
    }

    const deleteComment = (id) => {
        axios.delete(`${apiURL}/videos/${this.props.currentVideo.id}/comments/${id}/${apiKEY}`)
        .catch((error) => {
            console.log(error)
        })
    }

    return (
        <>
            <Description videoDetails={props.videoDetails}/>
            <CommentSection addComment={addComment} deleteComment={deleteComment} comments={props.videoDetails.comments}/>
        </>
    );
}
