import "./CommentSection.scss"
import CommentForm from "../CommentForm/CommentForm";
import Comment from "../Comment/Comment";
import axios from "axios";
const apiURL = 'https://project-2-api.herokuapp.com'
const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default function CommentSection(props) {

    const handleAdd = (comment) => {
        axios.post(`${apiURL}/videos/${props.videoDetails.id}/comments/${apiKEY}`, comment)
        .catch((error) => {
            console.log(error)
        })
    }

    const handleDelete = (id) => {
        axios.delete(`${apiURL}/videos/${props.videoDetails.id}/comments/${id}/${apiKEY}`)
        .catch((error) => {
            console.log(error)
        })
    }

    return (
        <>
            <p className="comment-count">{`${props.videoDetails.comments.length} Comments`}</p>
            <CommentForm addComment={handleAdd}/>
            {props.videoDetails.comments.sort((x, y) => {
                return y.timestamp - x.timestamp
            }).map((comment, index) => (
                <Comment deleteComment={handleDelete} key={index} comment={comment}/>
            ))}
        </>
    );
}
