import { Component } from 'react';
import '../../BrainFlix.scss';
import Video from '../../components/Video/Video';
import VideoDetails from '../../components/VideoDetails/VideoDetails';
import NextVideos from '../../components/NextVideos/NextVideos';
import axios from 'axios';
const apiURL = 'http://localhost:8080'

export default class Home extends Component{

  state = {
    currentVideo: {},
    currentVideoDetails: {
      timestamp: 0,
      comments: []
    },
    nextVideos: [],
    isLoading: true
  }

  componentDidMount() {
    axios.get(`${apiURL}/videos`)
    .then((response) => {
      this.setState({
        currentVideo: response.data.find((video) => {
          return video.id === this.props.match.params.id
        }),
        nextVideos: response.data.filter((video) => {
          return video.id !== this.props.match.params.id
        })
      })
      return this.state.currentVideo
    })
    .then((video) => {
      axios.get(`${apiURL}/videos/${video.id}`)
      .then((response) => {
        this.setState({
          currentVideoDetails: response.data,
          isLoading: false
        })
      })
      .catch((error) => {
          console.log(error)
      })
    })
    .catch((error) => {
      console.log(error)
    })
  }


