import {Component} from "react"
import "./VideoCard.scss"
import { Link } from 'react-router-dom'

export default function VideoCard(props) {

    const handleClick = (index) => {
        this.props.clickHandler(index)
    }

    return (
        <div onClick={() => {this.handleClick(this.props.currentVideo.id)}} className="video-card">
            <div>
                <img src={this.props.currentVideo.image} className="video-card__thumbnail" alt="thumbnail"></img>
            </div>
            <div className="video-card__text">
                <p className="video-card__title">{this.props.currentVideo.title}</p>
                <p className="video-card__channel">{this.props.currentVideo.channel}</p>
            </div>
        </div>
    );
}
