import React from "react";
import "./Video.scss"
import data from "../../data/video-details.json"
import Description from '../Description/Description';
import CommentForm from "../CommentForm/CommentForm";
import Comment from "../Comment/Comment";

export default class Video extends React.Component {

  state = {
    currentVideo: data[0],
    comments: [...currentVideo.comments]
  }

  addComment = () => {

  }

  render() {
    return (
      <>
      <div className="video-container">
        <video 
        className="video" 
        controls 
        poster={this.state.currentVideo.image}>
        </video>
      </div>
      <main>
        <Description
        title={this.state.currentVideo.title}
        channel={this.state.currentVideo.channel}
        timestamp={this.state.currentVideo.timestamp}
        views={this.state.currentVideo.views}
        likes={this.state.currentVideo.likes}
        description={this.state.currentVideo.description}
        />
        {/* Comment Form */}
        <CommentForm addComment={this.addComment}/>
        {/* Comments */}
        {this.state.currentVideo.comments.map((comment) => (
          <Comment
          name={comment.name}
          comment={comment.comment}
          likes={comment.likes}
          timestamp={comment.timestamp}
          />
        ))}
      </main>
      </>
    );
  }
}
