import {Component} from "react"
import "./CommentSection.scss"
import CommentForm from "./CommentForm/CommentForm";
import Comment from "./Comment/Comment";
import axios from "axios";
const apiURL = 'https://project-2-api.herokuapp.com'
const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default class CommentSection extends Component {

    state = {
        comments: {}
    }

    componentDidMount() {
        axios.get(`${apiURL}/videos/${this.props.currentVideo.id}/${apiKEY}`)
        .then((response) => {
            this.setState({comments: response.comments})
        })
        .catch((error) => {
            console.log(error)
        })
    }

    addComment = (comment) => {
        //Will be implemented in future sprint
    }

    render() {
        return (
        <>
            {/* Comment Counter */}
            <p className="comment-count">{`${this.state.comments.length} Comments`}</p>
            {/* Comment Form */}
            <CommentForm addComment={this.addComment}/>
            {/* Comments */}
            {this.state.comments.map((comment, index) => (
                <Comment key={index} comment={comment}/>
            ))}
        </>);
    }
}
