import {Component} from "react"
import "./NextVideos.scss"
import VideoCard from "./VideoCard/VideoCard";
import axios from "axios";
import { BrowserRouter, Link } from "react-router-dom";
const apiURL = 'https://project-2-api.herokuapp.com'
const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default class NextVideos extends Component {

    state = {
        data: {}
    }

    componentDidMount(){
        {console.log(this.props)}
        axios.get(`${apiURL}/videos/${this.props.match.params.id}/${apiKEY}`)
        .then((response) => {
          this.setState({data: response.data})
        })
        .catch((error) => {
          console.log(error)
        })
    }


    render() {
        return (
            <div className="videos-container">
                <p className="header-text">NEXT VIDEOS</p>
                {console.log(this.props.match.params.id)}
                {console.log(this.state.data)}
                {console.log(this.props.data)}
                {this.state.data.filter((video) => (video !== this.props.currentVideo))
                .map((video, index) => (
                    <Link to={this.props.match.path + `${this.state.data.id}`}>
                        <VideoCard
                        key={index}
                        clickHandler={this.props.clickHandler}
                        id={this.state.data.indexOf(video)}
                        video={video}/>
                    </Link>
                ))}    
            </div>
        );
    }
}
