import {Component} from "react"
import "./NextVideos.scss"
import VideoCard from "./VideoCard/VideoCard";

export default class NextVideos extends Component {

    componentDidMount() {
        axios.get(`${apiURL}/videos/${apiKEY}`)
    .then((response) => {
      console.log(response.data)
      this.setState({currentVideo: response.data[0]})
    })
    .catch((error) => {
      console.log(error)
    })
    }

    render() {
        return (
        <div className="videos-container">
            <p className="header-text">NEXT VIDEOS</p>
            {this.props.data.filter((video) => (video !== this.props.currentVideo))
            .map((video, index) => (
                <VideoCard
                key={index}
                clickHandler={this.props.clickHandler}
                id={this.props.data.indexOf(video)}
                video={video}/>
            ))}
        </div>);
    }
}
