import { Component } from 'react'
import "./Description.scss"
import ReactTimeAgo from "react-time-ago";
import axios from "axios";
const apiURL = 'https://project-2-api.herokuapp.com'
const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default class Description extends Component {

    state = {
        videoDetails: { timestamp: null }
    }

    componentDidMount() {
        axios.get(`${apiURL}/videos/${this.props.currentVideo.id}/${apiKEY}`)
        .then((response) => {
            this.setState({videoDetails: response.data})
        })
        .catch((error) => {
            console.log(error)
        })
    }

    componentDidUpdate(prevState) {
        if(prevState.videoDetails !== this.state.videoDetails) {
            this.componentDidMount()
        }
    }

    render() {
        return (
            <div className="container">
                <h1 className="title">{this.state.videoDetails.title}</h1>
                <div className="insights">
                    <div className="video-info">
                        <span className="video-info__channel">{`By ${this.state.videoDetails.channel}`}</span>
                        <span className="video-info__date">{<ReactTimeAgo date={this.state.videoDetails.timestamp} locale={"en-US"}/>}</span>
                        <span className="video-info__date">{}</span>
                    </div>
                    <div className="interactions">
                        <span className="interactions__views">
                            <div className="interactions__icon"></div>
                            {this.state.videoDetails.views}
                        </span>
                        <span className="interactions__likes">
                            <div className="interactions__icon--likes"></div>
                            {this.state.videoDetails.likes}
                        </span>
                    </div>
                </div>
                <p className="description">{this.state.videoDetails.description}</p>
            </div>
        );
    }

}
