    render() {
        return (
            <div className="container">
                <h1 className="title">{this.props.videoDetails.title}</h1>
                <div className="insights">
                    <div className="video-info">
                        <span className="video-info__channel">{`By ${this.props.videoDetails.channel}`}</span>
                        <span className="video-info__date">{<ReactTimeAgo date={this.props.videoDetails.timestamp} locale={"en-US"}/>}</span>
                        <span className="video-info__date">{}</span>
                    </div>
                    <div className="interactions">
                        <span className="interactions__views">
                            <div className="interactions__icon"></div>
                            {this.props.videoDetails.views}
                        </span>
                        <span className="interactions__likes">
                            <div className="interactions__icon--likes"></div>
                            {this.props.videoDetails.likes}
                        </span>
                    </div>
                </div>
                <p className="description">{this.props.videoDetails.description}</p>
            </div>
        );
    }

}
