import { Component } from "react"
import "./CommentSection.scss"
import CommentForm from "./CommentForm/CommentForm";
import Comment from "./Comment/Comment";
// import axios from "axios";
// const apiURL = 'https://project-2-api.herokuapp.com'
// const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default class CommentSection extends Component {

    state = {
        comments: []
    }

    // componentDidMount() {
    //     axios.get(`${apiURL}/videos/${this.props.currentVideo.id}/${apiKEY}`)
    //     .then((response) => {
    //         this.setState({comments: response.data.comments})
    //     })
    //     .catch((error) => {
    //         console.log(error)
    //     })
    // }

    // componentDidUpdate(prevState) {
    //     if(prevState.comments !== this.state.comments) {
    //         this.componentDidMount()
    //     }
    // }

    addComment = (comment) => {
        axios.post(`${apiURL}/videos/${this.props.currentVideo.id}/comments/${apiKEY}`, comment)
        .catch((error) => {
            console.log(error)
        })
    }

    deleteComment = (id) => {
        axios.delete(`${apiURL}/videos/${this.props.currentVideo.id}/comments/${id}/${apiKEY}`)
        .catch((error) => {
            console.log(error)
        })
    }

    render() {
        return (
        <>
            <p className="comment-count">{`${this.state.comments.length} Comments`}</p>
            <CommentForm addComment={this.addComment}/>
            {this.state.comments.sort((x, y) => {
                return y.timestamp - x.timestamp
            }).map((comment, index) => (
                <Comment deleteComment={this.deleteComment} key={index} comment={comment}/>
            ))}
        </>);
    }
}
