import { Component } from 'react';
import '../../BrainFlix.scss';
import Video from '../../components/Video/Video';
// import Description from '../../components/Description/Description';
// import CommentSection from '../../components/CommentSection.js/CommentSection';
import NextVideos from '../../components/NextVideos/NextVideos';
import data from '../../data/video-details.json'
import axios from 'axios';
// import { BrowserRouter, Switch, Route } from 'react-router-dom';
const apiURL = 'https://project-2-api.herokuapp.com'
const apiKEY = '?api_key=67e1a87f-213f-4fba-b06e-69697646e796'

export default class Home extends Component {

  state = {
    currentVideo: {},
    data: {},
    isLoading: true
  }

  componentDidMount() {
    axios.get(`${apiURL}/videos/${apiKEY}`)
    .then((response) => {
      this.setState({currentVideo: response.data[0]}, () => {
        const filteredArray = response.data.filter((video) => {
          return video.id !== this.state.currentVideo.id
        })
        this.setState({data: filteredArray})
      })
    })
    .catch((error) => {
      console.log(error)
    })
  }

  showResults = () => {
    if(this.state.isLoading) {
      return (
        <>
        <Video currentVideo={this.state.currentVideo}/>
        </>
      )
    } else {
      return <p>Loading...</p>
    }
  }

  // componentDidUpdate(prevProps, prevState) {
  //   console.log(prevProps, prevState)
  // }

  selectVideo = (index) => {
    this.setState({currentVideo: data[index]})
  }

  render() {
    return (
      <>
      {this.showResults()}
      {/* <Video currentVideo={this.state.currentVideo}/> */}
      <main>
        <div className="left-containter">
          {/* <Description currentVideo={this.state.currentVideo}/>
          <CommentSection currentVideo={this.state.currentVideo}/> */}
        </div>
        <NextVideos
        data={this.state.data}
        clickHandler={this.selectVideo} 
        currentVideo={this.state.currentVideo}
        />
      </main>
      </>
    );
  }
}
